INSERT INTO `user_queries` (`id`, `name`, `description`, `sqlquery`, `userAdmin`, `userIdAdmin`, `timeStamp`) VALUES
(16, 'Show_Tables', 'List all the tables in the CTC database (table names only).', 'SHOW tables', 's.pearson', 545, '2008-04-05 03:35:40'),
(29, 'List_Subs_Payment_Status', 'A list of all active membership showing their payment status for subscription year = 2008.', 'SELECT id as ID, \n    mships.*,\n    amountPaid as Amount,\n    cardNumber as CardNum, secondaryCardnumber as Card2Num, \n    paymentDate as DatePaid, notes as Notes \n\nFROM\n   (SELECT membershipId as MSID,\n                 group_concat(concat(firstName, \' \', lastName) separator \' & \') as MembershipName,\n                 membershipTypeEnum as membershipType, loginName as Login\n    FROM members, memberships, membership_types\n    WHERE membershipId = memberships.id\n    AND      membershipTypeId = membership_types.id\n    AND      statusAdmin = \'Active\'\n    GROUP BY MSID\n   )  AS mships\n\nLEFT JOIN \n   (SELECT * FROM subs_payments\n    WHERE subscriptionYear = \'2008\'\n    AND NOT deletedBoolAdmin\n   ) as subs\n\nON   MSID = subs.membershipId\nORDER BY MembershipName', 's.pearson', 545, '2008-04-13 03:46:16'),
(31, 'New_Members', 'A list of all members who joined in the past year', 'SELECT \n     concat(firstName, \' \', lastName) as name,\n     membershiptype,\n     date(dateJoined) as \'Date Joined\'\nFROM  view_members\nWHERE status =\'Active\'\nAND datediff(now(), dateJoined) < 365\nORDER BY dateJoined DESC', 's.pearson', 545, '2008-04-14 20:30:25'),
(34, 'List_Payment_Updates', 'A listing of changes made to subs_payments entries. Does not include new entries.', 'SELECT mailName as Name, \n    fieldName as Field,\n    oldValue as Old,\n    newValue as New, \n    subs_payments_updates.timestampAdmin as Date, \n    subs_payments_updates.notes as Notes\n FROM subs_payments, subs_payments_updates, view_memberships vm\n\n WHERE vm.membershipId= subs_payments.membershipId\n AND subs_payments.id = subs_payments_updates.subsPaymentsId\n\n ORDER BY Date DESC', 's.pearson', 545, '2008-04-21 09:19:58'),
(35, 'Recent_Address_Changes', 'A list of recent changes of address', 'SELECT DISTINCT concat(firstName, \' \', lastName) as Name, \n    concat(address1,\', \',address2,\', \',city,\' \',postcode) as address,\n    Date(membership_updates.timeStampAdmin) as DateChanged\n\n FROM members, membership_updates, memberships\n WHERE memberships.id = membership_updates.membershipId \n AND memberships.id = members.membershipId\n AND (membership_updates.fieldName = \'address1\'\n      or membership_updates.fieldName = \'address2\'\n      or membership_updates.fieldName = \'city\'\n      or membership_updates.fieldName = \'postcode\')\n\nORDER BY DateChanged DESC', 's.pearson', 545, '2008-04-22 09:51:49'),
(38, 'List_Recent_Updates', 'A listing of all updates to the members and membership tables, most recent changes first, in the last 90 days.', '(SELECT concat(members.firstName, \' \', members.lastName) as Name, fieldName as Field, oldValue as Old,\n    newValue as New, member_updates.timestampAdmin as Date, member_updates.notes as Notes, \nadmins.loginName as WhoDunnit\n FROM members, member_updates, members as admins\n WHERE members.id = member_updates.memberId\n AND  member_updates.userIdAdmin = admins.id\n AND datediff(now(), member_updates.timeStampAdmin) <= 90)\n\nUNION\n\n(SELECT concat(members.firstName, \' \', members.lastName) as Name, fieldName as Field, oldValue as Old,\n    newValue as New,  membership_updates.timestampAdmin as Date,\n    membership_updates.notes as Notes, \n    admins.loginName as WhoDunnit\n FROM members, membership_updates, memberships, members as admins\n WHERE memberships.id = membership_updates.membershipId \n AND memberships.id = members.membershipId\n AND membership_updates.userIdAdmin = admins.id\n AND datediff(now(), membership_updates.timeStampAdmin) <= 90)\n\n ORDER BY Date DESC', '_menu_', 0, '2008-05-07 04:04:54'),
(39, 'List_Payments', 'A list of all subscription payments and the more-vital associated membership information.', 'SELECT \n    vm.membershipId as MSID,\n    mailName,\n    amountPaid as Amount,\n    cardNumber as CardNum, secondaryCardnumber as Card2Num, \n    subscriptionYear as SubsYear,\n    sp.paymentDate as DatePaid, sp.notes as Notes \n\nFROM\n   view_memberships as vm, subs_payments as sp\nWHERE  vm.membershipId = sp.membershipId\nAND NOT deletedBoolAdmin\n\nORDER BY subscriptionYear DESC, mailName', '_menu_', 0, '2008-05-07 04:06:21'),
(43, 'Show_Members', 'Show a subset of the information from joining the members, memberships and membership_types tables. Includes both active and inactive members.', 'SELECT members.id as \'ID\', \n            concat(lastName, \', \', firstName) as \'Name\', \n            membershipTypeEnum as \'Type\', \n            statusAdmin as \'Status\',\n            concat(address1, \', \', address2, \', \', city) as \'Address\', \n            homePhone as \'Phone\'\nFROM  members, memberships, membership_types \nWHERE membershipId = memberships.id\nAND memberships.membershipTypeId = membership_types.id\nORDER BY Name', 'r.lobb', 595, '2008-06-08 21:22:28'),
(44, 'List_Memberships', 'List all information for all active or pending memberships', 'SELECT * FROM view_memberships\nWHERE status = \'Active\' or status = \'Provisional\'\nORDER BY mailName', 'r.lobb', 595, '2008-06-08 21:22:28'),
(45, 'List_Membership_Types', 'List all fields of all rows of the membership_types table.', 'SELECT * from membership_types', 'r.lobb', 595, '2008-06-08 21:22:28'),
(46, 'List_Membership_Updates', 'List all fields of all rows of the membership_updates table', 'SELECT * from membership_updates', 'r.lobb', 595, '2008-06-08 21:22:28'),
(47, 'List_Members', 'List all fields for all active or pending members', 'SELECT  * from view_members \nwhere (status = \'Active\' or status = \'Pending\')\nORDER BY lastName, firstName', 'r.lobb', 595, '2008-06-08 21:22:28'),
(48, 'Show_Tables', 'List all the tables in the CTC database (table names only).', 'SHOW tables', 'r.lobb', 595, '2008-06-08 21:22:28'),
(49, 'Recent_Resignations', 'List all membership closures (resignations, strike-offs, couplings, decouplings ...) in the past 365 days.', 'SELECT concat(lastName, \', \', firstName) as \'Name\', membershipTypeEnum as \'Type\', \n    statusAdmin as \'Status\', date(resignationDateAdmin) as \'Resignation date\', date(dateClosedAdmin) as \'Date Closed\'\nFROM  members, memberships, membership_types\nWHERE membershipId = memberships.id\nAND memberships.membershipTypeId = membership_types.id\nAND not(statusAdmin =\'Active\')\nAND datediff(now(), dateClosedAdmin) < 365\nORDER BY dateClosedAdmin DESC', 'r.lobb', 595, '2008-06-08 21:22:28'),
(51, 'List_Subs_Payment_Status', 'A list of all active membership showing their payment status for subscription year = 2008.', 'SELECT id as ID, \n    mships.*,\n    amountPaid as Amount,\n    cardNumber as CardNum, secondaryCardnumber as Card2Num, \n    paymentDate as DatePaid, notes as Notes \n\nFROM\n   (SELECT membershipId as MSID,\n                 group_concat(concat(firstName, \' \', lastName) separator \' & \') as MembershipName,\n                 membershipTypeEnum as membershipType, loginName as Login\n    FROM members, memberships, membership_types\n    WHERE membershipId = memberships.id\n    AND      membershipTypeId = membership_types.id\n    AND      statusAdmin = \'Active\'\n    GROUP BY MSID\n   )  AS mships\n\nLEFT JOIN \n   (SELECT * FROM subs_payments\n    WHERE subscriptionYear = \'2008\'\n    AND NOT deletedBoolAdmin\n   ) as subs\n\nON   MSID = subs.membershipId\nORDER BY MembershipName', 'r.lobb', 595, '2008-06-08 21:22:28'),
(52, 'List_Member_Updates', 'List all rows of the member_updates table', 'SELECT * from member_updates', 'r.lobb', 595, '2008-06-08 21:22:28'),
(53, 'New_Members', 'A list of all member(ships) created in the past year', 'SELECT concat(lastName, \', \', firstName) as \'Name\', membershipTypeEnum as \'Type\',\n          date(dateJoined) as \'Date Joined\'\nFROM  members, memberships, membership_types\nWHERE membershipId = memberships.id\nAND memberships.membershipTypeId = membership_types.id\nAND statusAdmin =\'Active\'\nAND datediff(now(), dateJoined) < 365\nORDER BY dateJoined desc', 'r.lobb', 595, '2008-06-08 21:22:28'),
(54, 'List_Payment_Updates', 'A listing of changes made to subs_payments entries. Does not include new entries.', 'SELECT mailName as Name, \n    fieldName as Field,\n    oldValue as Old,\n    newValue as New,\n    subs_payments_updates.timestampAdmin as Date, \n    subs_payments_updates.notes as Notes,\n    subs_payments_updates.userAdmin as WhoDunnit\n FROM subs_payments, subs_payments_updates, memberships_view ms\n WHERE\n     ms.membershipId = subs_payments.membershipId\n AND subs_payments.id = subs_payments_updates.subsPaymentsId\n\n ORDER BY Date DESC', 'r.lobb', 595, '2008-06-08 21:22:28'),
(55, 'Recent_Address_Changes', 'A list of recent changes of address', 'SELECT DISTINCT concat(firstName, \' \', lastName) as Name, \n    concat(address1,\', \',address2,\', \',city,\' \',postcode) as address,\n    Date(membership_updates.timeStampAdmin) as DateChanged\n\n FROM members, membership_updates, memberships\n WHERE memberships.id = membership_updates.membershipId \n AND memberships.id = members.membershipId\n AND (membership_updates.fieldName = \'address1\'\n      or membership_updates.fieldName = \'address2\'\n      or membership_updates.fieldName = \'city\'\n      or membership_updates.fieldName = \'postcode\')\n\nORDER BY DateChanged DESC', 'r.lobb', 595, '2008-06-08 21:22:28'),
