(62, 'Recent_Resignations', 'List all membership closures (resignations, strike-offs, couplings, decouplings ...) in the past 365 days.', 'SELECT concat(lastName, \', \', firstName) as \'Name\', membershipTypeEnum as \'Type\', \n    statusAdmin as \'Status\', date(resignationDateAdmin) as \'Resignation date\', date(dateClosedAdmin) as \'Date Closed\'\nFROM  members, memberships, membership_types\nWHERE membershipId = memberships.id\nAND memberships.membershipTypeId = membership_types.id\nAND not(statusAdmin =\'Active\')\nAND datediff(now(), dateClosedAdmin) < 365\nORDER BY dateClosedAdmin DESC', 'j.harlow', 405, '2008-06-08 21:22:51'),
(63, 'Unpaid_Memberships', 'A list of all active memberships that are not in good financial standing for the subscription year 2008. This list does not include life members, regardless of whether or not they have been issued with a card number.', 'SELECT\n    mships.*\n\nFROM\n\n   (SELECT membershipId as MSID,\n                 group_concat(concat(firstName, \' \', lastName) separator \' & \') as MembershipName,\n                 membershipTypeEnum as membershipType, loginName as Login\n    FROM members, memberships, membership_types\n    WHERE membershipId = memberships.id\n    AND      membershipTypeId = membership_types.id\n    AND      statusAdmin = \'Active\'\n    GROUP BY MSID\n   )  AS mships\n\nLEFT JOIN \n\n   (SELECT * FROM subs_payments\n    WHERE subscriptionYear = \'2008\'\n     AND NOT deletedBoolAdmin\n   ) AS subs\n\nON   MSID = subs.membershipId\n\nWHERE paymentDate IS NULL\nAND NOT (membershipType = \'Life\')\nAND NOT (membershipType = \'LifeCouple\')\nORDER BY MembershipName', 'j.harlow', 405, '2008-06-08 21:22:51'),
(64, 'List_Subs_Payment_Status', 'A list of all active membership showing their payment status for subscription year = 2008.', 'SELECT id as ID, \n    mships.*,\n    amountPaid as Amount,\n    cardNumber as CardNum, secondaryCardnumber as Card2Num, \n    paymentDate as DatePaid, notes as Notes \n\nFROM\n   (SELECT membershipId as MSID,\n                 group_concat(concat(firstName, \' \', lastName) separator \' & \') as MembershipName,\n                 membershipTypeEnum as membershipType, loginName as Login\n    FROM members, memberships, membership_types\n    WHERE membershipId = memberships.id\n    AND      membershipTypeId = membership_types.id\n    AND      statusAdmin = \'Active\'\n    GROUP BY MSID\n   )  AS mships\n\nLEFT JOIN \n   (SELECT * FROM subs_payments\n    WHERE subscriptionYear = \'2008\'\n    AND NOT deletedBoolAdmin\n   ) as subs\n\nON   MSID = subs.membershipId\nORDER BY MembershipName', 'j.harlow', 405, '2008-06-08 21:22:51'),
(65, 'List_Member_Updates', 'List all rows of the member_updates table', 'SELECT * from member_updates', 'j.harlow', 405, '2008-06-08 21:22:51'),
(66, 'New_Members', 'A list of all member(ships) created in the past year', 'SELECT concat(lastName, \', \', firstName) as \'Name\', membershipTypeEnum as \'Type\',\n          date(dateJoined) as \'Date Joined\'\nFROM  members, memberships, membership_types\nWHERE membershipId = memberships.id\nAND memberships.membershipTypeId = membership_types.id\nAND statusAdmin =\'Active\'\nAND datediff(now(), dateJoined) < 365\nORDER BY dateJoined desc', 'j.harlow', 405, '2008-06-08 21:22:51'),
(67, 'List_Payment_Updates', 'A listing of changes made to subs_payments entries. Does not include new entries.', 'SELECT MembershipName as Name, \n    fieldName as Field,\n    oldValue as Old,\n    newValue as New, subs_payments_updates.timestampAdmin as Date, \n    subs_payments_updates.notes as Notes\n FROM subs_payments, subs_payments_updates,\n   (SELECT membershipId as MSID,\n                 group_concat(concat(firstName, \' \', lastName) separator \' & \') as MembershipName\n    FROM members, memberships\n    WHERE membershipId = memberships.id\n    GROUP BY MSID\n   )  AS mships\n\n WHERE MSID = subs_payments.membershipId\n AND subs_payments.id = subs_payments_updates.subsPaymentsId\n\n ORDER BY Date DESC', 'j.harlow', 405, '2008-06-08 21:22:51'),
(68, 'Recent_Address_Changes', 'A list of recent changes of address', 'SELECT DISTINCT concat(firstName, \' \', lastName) as Name, \n    concat(address1,\', \',address2,\', \',city,\' \',postcode) as address,\n    Date(membership_updates.timeStampAdmin) as DateChanged\n\n FROM members, membership_updates, memberships\n WHERE memberships.id = membership_updates.membershipId \n AND memberships.id = members.membershipId\n AND (membership_updates.fieldName = \'address1\'\n      or membership_updates.fieldName = \'address2\'\n      or membership_updates.fieldName = \'city\'\n      or membership_updates.fieldName = \'postcode\')\n\nORDER BY DateChanged DESC', 'j.harlow', 405, '2008-06-08 21:22:51'),
(71, 'Email_List_Members', 'Primary and secondary email addresses for all active members who have a primary email.', 'SELECT concat(vm.firstName,\' \',vm.lastName) as Name, vm.primaryEmail, secondaryEmail\nFROM view_members as vm, members\nWHERE Status=\'Active\'\nAND memberId = members.id\nAND not (vm.primaryEmail is NULL or vm.primaryEmail=\'\')', 's.pearson', 545, '2008-06-09 22:56:38'),
(77, 'Email_List_Bulk', 'An email list of all unique primary emails, for bulk mail outs. It contains every member\'s primary email address except that where couples share a primary email address, a single email address with a composite name is generated.', 'SELECT MemberID, Name, Email FROM\n  (SELECT group_concat(concat(FirstName,\' \',LastName) SEPARATOR \' & \') as Name, \n      members.id as MemberID,\n      primaryEmail as Email\n   FROM members, memberships\n   WHERE membershipId = memberships.id\n   AND statusAdmin=\'Active\'\n   AND onEmailListBool=\'Yes\'\n   AND NOT (primaryEmail is NULL or primaryEmail=\'\')\n   GROUP BY primaryEmail) as UniqueEmails\n\nORDER BY Name', 'r.lobb', 595, '2008-06-10 02:47:02'),
(78, 'Email_List_Members', 'Primary and secondary email addresses for all active members who have a primary email.', 'SELECT concat(FirstName,\' \',LastName) as Name, primaryEmail, secondaryEmail\nFROM members, memberships\nWHERE membershipId = memberships.id\nAND statusAdmin=\'Active\'\n\nAND not (primaryEmail is NULL or primaryEmail=\'\')', 'r.lobb', 595, '2008-06-10 02:47:02'),
(81, 'Mail_Associates', 'Mailing address info for all associate memberships that receive a newsletter. Includes subscription payment status for the current subscription year (taken to be the calendar year with a 3-month offset).', 'SELECT\n\n    IF(givenMembershipName = \'\' OR givenMembershipName IS NULL, \n            derivedMembershipName,\n            givenMembershipName) as name, \n    address1, address2, city, postcode, membershipType,\n    IF(ISNULL(paymentDate),  \'Unpaid\',\'\') as Paid\n\nFROM\n\n    (SELECT \n          membershipId as MSID,\n          group_concat(concat(firstName,\' \', lastName) separator \' & \')  as derivedMembershipName,\n          membershipMailName as givenMembershipName,\n          address1, address2, city, postcode, membershipTypeEnum as membershipType\n      FROM members, memberships, membership_types\n      WHERE memberships.id = membershipId \n      AND membershipTypeId = membership_types.id\n      AND statusAdmin = \'Active\'\n      AND (membershipTypeEnum like \'Associate%\')\n      AND mailNewsletterBool = \'Yes\'\n      GROUP BY membershipId\n    ) AS memberships\n\n   LEFT JOIN\n\n       (SELECT * FROM subs_payments\n       WHERE  subscriptionYear = YEAR(DATE_SUB(NOW(), INTERVAL 3 MONTH))\n       AND NOT deletedBoolAdmin)  AS subs\n      \n    ON MSID = subs.membershipId\n\n\nORDER BY name', 'r.lobb', 595, '2008-06-10 02:47:02'),
(82, 'Mail_FMC', 'Mail addresses for all active memberships that get an FMC mag but no newsletter.', 'SELECT\n\n    IF(givenMembershipName = \'\' OR givenMembershipName IS NULL, \n            derivedMembershipName,\n            givenMembershipName) as name, \n    address1, address2, city, postcode, membershipType,\n    IF(ISNULL(paymentDate) AND NOT (membershipType = \'Life\' OR membershipType = \'LifeCouple\'), \n        \'Unpaid\',\'\') as Paid\n\nFROM\n\n    (SELECT \n          membershipId as MSID,\n          group_concat(concat(firstName,\' \', lastName) separator \' & \')  as derivedMembershipName,\n          membershipMailName as givenMembershipName,\n          address1, address2, city, postcode, membershipTypeEnum as membershipType\n      FROM members, memberships, membership_types\n      WHERE memberships.id = membershipId \n      AND membershipTypeId = membership_types.id\n      AND statusAdmin = \'Active\'\n      AND not (membershipTypeEnum like \'Associate%\')\n      AND mailNewsletterBool = \'No\'\n      AND mailFMCBool = \'Yes\'\n      GROUP BY membershipId\n    ) AS memberships\n\n   LEFT JOIN\n\n       (SELECT * FROM subs_payments\n       WHERE  subscriptionYear = YEAR(DATE_SUB(NOW(), INTERVAL 3 MONTH))\n       AND NOT deletedBoolAdmin)  AS subs\n      \n    ON MSID = subs.membershipId\n\n\nORDER BY name', 'r.lobb', 595, '2008-06-10 02:47:02'),
(83, 'Mail_Newsletter_NoFmc', 'Mailing list for non-associate memberships that get a newsletter but no FMC mag. This is needed on FMC bulletin mailout runs so that these people still get a newsletter despite not getting an FMC mag.', 'SELECT\n\n    IF(givenMembershipName = \'\' OR givenMembershipName IS NULL, \n            derivedMembershipName,\n            givenMembershipName) as name, \n    address1, address2, city, postcode, membershipType,\n    IF(ISNULL(paymentDate) AND NOT (membershipType = \'Life\' OR membershipType = \'LifeCouple\'), \n        \'Unpaid\',\'\') as Paid\n\nFROM\n\n    (SELECT \n          membershipId as MSID,\n          group_concat(concat(firstName,\' \', lastName) separator \' & \')  as derivedMembershipName,\n          membershipMailName as givenMembershipName,\n          address1, address2, city, postcode, membershipTypeEnum as membershipType\n      FROM members, memberships, membership_types\n      WHERE memberships.id = membershipId \n      AND membershipTypeId = membership_types.id\n      AND statusAdmin = \'Active\'\n      AND not (membershipTypeEnum like \'Associate%\')\n      AND mailNewsletterBool = \'Yes\'\n      AND mailFMCBool = \"No\"\n      GROUP BY membershipId\n    ) AS memberships\n\n   LEFT JOIN\n\n       (SELECT * FROM subs_payments\n       WHERE  subscriptionYear = YEAR(DATE_SUB(NOW(), INTERVAL 3 MONTH))\n       AND NOT deletedBoolAdmin)  AS subs\n      \n    ON MSID = subs.membershipId\n\n\nORDER BY name', 'r.lobb', 595, '2008-06-10 02:47:02'),
(86, 'List_Members', 'A list of all active and pending (provisional) members and their details.', 'SELECT  * from view_members \nwhere (status = \'Active\' or status = \'Pending\')\nORDER BY lastName, firstName', '_menu_', 0, '2008-06-26 01:57:24'),
(87, 'Example_Query', 'An example query, which is the same as the one you get if you click the \"New Query\" link. It displays selected information for all members in the database, whether active, resigned, deceased or whatever.', 'SELECT members.id as \'ID\',\n     concat(lastName, \', \', firstName) as \'Name\',\n     membershipTypeEnum as \'Type\',\n     statusAdmin as \'Status\', \n     concat(address1, \', \', address2, \', \', city) as \'Address\',\n     homePhone as \'Phone\'\nFROM  members, memberships, membership_types\nWHERE membershipId = memberships.id\nAND memberships.membershipTypeId = membership_types.id\nORDER BY Name', 'r.vink', 1005, '2008-06-28 10:50:31'),
(88, 'List_Memberships', 'A list of all active and provisional memberships and their detail.', 'SELECT * FROM view_memberships\nWHERE status = \'Active\' or status = \'Provisional\'\nORDER BY mailName', '_menu_', 0, '2008-07-15 11:33:47'),
(89, 'Mail_Associates', 'Mailing address info for all associate memberships that receive a newsletter. Includes subscription payment status for the current subscription year (taken to be the calendar year with a 3-month offset).', 'SELECT \n    mailName as name, \n    address1, address2, city, postcode, type,\n    IF(paid <> \'Yes\',  \'Unpaid\',\'\') as Paid\n\nFROM\n    view_memberships\n\nWHERE  status = \'Active\'\n      AND (type like \'Associate%\')\n      AND mailNewsletter = \'Yes\'\n\nORDER BY name', 's.pearson', 545, '2008-07-16 05:26:57'),
(90, 'Mail_FMC', 'Mail addresses for all active memberships that get an FMC mag ONLY', 'SELECT \n   mailName as name, \n    address1, address2, city, postcode, type,\n    IF(paid <> \'Yes\',\'Unpaid\',\'\') as Paid\n\nFROM view_memberships\n      WHERE status = \'Active\'\n      AND not (type like \'Associate%\')\n      AND mailNewsletter = \'No\'\n      AND mailFMC = \'Yes\'\n\nORDER BY name', 's.pearson', 545, '2008-07-16 05:27:59'),
(91, 'Mail_FMC_Cards', 'This needs to be run once a year to get everyone who gets no other mailings but the FMC Cards', 'SELECT \n\n    mailName as name, \n    address1, address2, city, postcode, type,\n    IF(paid = \'No\', \'Unpaid\',\'\') as Paid\n\nFROM\n    view_memberships\n\nWHERE status = \'Active\'\n            AND mailNewsletter = \'No\'\n      AND mailFMC = \'No\'\n\nORDER BY name', 's.pearson', 545, '2008-07-16 05:53:04'),
(92, 'Mail_Newsletter_NoFmc', 'Mailing list for non-associate memberships that get a newsletter but no FMC mag. This is needed on FMC bulletin mailout runs so that these people still get a newsletter despite not getting an FMC mag.', 'SELECT \n\n    mailName as name, \n    address1, address2, city, postcode, type,\n    IF(paid = \'No\', \'Unpaid\',\'\') as Paid\n\nFROM\n    view_memberships\n\nWHERE status = \'Active\'\n      AND not (type like \'Associate%\')\n      AND mailNewsletter = \'Yes\'\n      AND mailFMC = \'No\'\n\nORDER BY name', 's.pearson', 545, '2008-07-16 05:54:46'),
(93, 'Mail_Non_Associates_Newsletter', 'Mailing list for non-associate memberships that get a newsletter. Some of these do NOT get an FMC bulletin', 'SELECT \n\n    mailName as name, \n    address1, address2, city, postcode, type,\n    IF(paid = \'No\', \'Unpaid\',\'\') as Paid\n\nFROM\n    view_memberships\n\nWHERE  status = \'Active\'\n      AND not (type like \'Associate%\')\n      AND mailNewsletter= \'Yes\'\n    \nORDER BY name', 's.pearson', 545, '2008-07-16 05:57:34'),
(94, 'Unpaid_Memberships', 'A list of all active memberships that are not in good financial standing for the current subscription year. This list does not include life members, regardless of whether or not they have been issued with a card number.', 'SELECT \n     membershipId as \'MembershipId\',\n     mailName as \'Name\',\n     isCouple,\n     memberId1,\n     memberId2,\n     type as \'Type\',\n     concat(address1, \', \', address2, \', \', city) as \'Address\',\n      primaryEmail,\n      homePhone\nFROM  view_memberships\nWHERE paid = \'No\'\nAND status=\'Active\'\nORDER BY Name', 's.pearson', 545, '2008-07-16 06:06:30'),
(95, 'List_Payments_With_Notes', 'Shows all entries in the subs_payments table where a note has been added.', 'select * from subs_payments where notes <> \'\'\n', 'r.lobb', 595, '2008-07-16 11:21:51'),
(103, 'Unpaid_Memberships', 'A list of all active memberships that are not in good financial standing for the subscription year 2008. This list does not include life members, regardless of whether or not they have been issued with a card number.', 'SELECT\n    mships.*\n\nFROM\n\n   (SELECT membershipId as MSID,\n                 group_concat(concat(firstName, \' \', lastName) separator \' & \') as MembershipName,\n                 membershipTypeEnum as membershipType, loginName as Login\n    FROM members, memberships, membership_types\n    WHERE membershipId = memberships.id\n    AND      membershipTypeId = membership_types.id\n    AND      statusAdmin = \'Active\'\n    GROUP BY MSID\n   )  AS mships\n\nLEFT JOIN \n\n   (SELECT * FROM subs_payments\n    WHERE subscriptionYear = \'2008\'\n     AND NOT deletedBoolAdmin\n   ) AS subs\n\nON   MSID = subs.membershipId\n\nWHERE paymentDate IS NULL\nAND NOT (membershipType = \'Life\')\nAND NOT (membershipType = \'LifeCouple\')\nORDER BY MembershipName', 'm.carr', 165, '2008-07-17 11:12:52'),
(104, 'List_Subs_Payment_Status', 'A list of all active membership showing their payment status for subscription year = 2008.', 'SELECT id as ID, \n    mships.*,\n    amountPaid as Amount,\n    cardNumber as CardNum, secondaryCardnumber as Card2Num, \n    paymentDate as DatePaid, notes as Notes \n\nFROM\n   (SELECT membershipId as MSID,\n                 group_concat(concat(firstName, \' \', lastName) separator \' & \') as MembershipName,\n                 membershipTypeEnum as membershipType, loginName as Login\n    FROM members, memberships, membership_types\n    WHERE membershipId = memberships.id\n    AND      membershipTypeId = membership_types.id\n    AND      statusAdmin = \'Active\'\n    GROUP BY MSID\n   )  AS mships\n\nLEFT JOIN \n   (SELECT * FROM subs_payments\n    WHERE subscriptionYear = \'2008\'\n    AND NOT deletedBoolAdmin\n   ) as subs\n\nON   MSID = subs.membershipId\nORDER BY MembershipName', 'm.carr', 165, '2008-07-17 11:12:52'),
(107, 'List_Payment_Updates', 'A listing of changes made to subs_payments entries. Does not include new entries.', 'SELECT mailName as Name, \n    fieldName as Field,\n    oldValue as Old,\n    newValue as New,\n    subs_payments_updates.timestampAdmin as Date, \n    subs_payments_updates.notes as Notes,\n    subs_payments_updates.userAdmin as WhoDunnit\n FROM subs_payments, subs_payments_updates, memberships_view ms\n WHERE\n     ms.membershipId = subs_payments.membershipId\n AND subs_payments.id = subs_payments_updates.subsPaymentsId\n\n ORDER BY Date DESC', 'm.carr', 165, '2008-07-17 11:12:52'),
(112, 'List_Payments', 'A list of all entries in the subs_payments table (joined to membership name for clarity).', 'SELECT   subscriptionYear as SubsYear,\n    id as ID, \n    mailName as Name,\n    amountPaid as Amount,\n    cardNumber as CardNum,\n    secondaryCardnumber as Card2Num, \n    paymentDate as DatePaid,\n    timeStampAdmin as DateEntered, \n    subs_payments.notes as Notes,\n    subs_payments.userAdmin as WhoDunnit\n\nFROM  subs_payments, memberships_view msv\nWHERE  status = \'Active\'\nAND msv.membershipId = subs_payments.membershipId\nAND NOT deletedBoolAdmin\n\nORDER BY SubsYear DESC, Name', 'm.carr', 165, '2008-07-17 11:12:52'),
(116, 'List_Payments_With_Notes', 'Shows all entries in the subs_payments table where a note has been added.', 'select * from subs_payments where notes <> \'\'\n', 'm.carr', 165, '2008-07-17 11:12:52'),
(117, 'Unpaid_Memberships', 'A list of all active memberships that are not in good financial standing for the current subscription year. This list does not include life members, regardless of whether or not they have been issued with a card number.', 'SELECT \n     membershipId,\n     mailName,\n     nameBySurname,\n     isCouple,\n     memberId1,\n     login1,\n     memberId2,\n     login2,\n     type as \'membershipType\',\n     concat(address1, \', \', address2, \', \', city) as \'Address\',\n     address1,\n     address2,\n     postcode,\n     city,\n     primaryEmail as email,\n     homePhone,\n     mailNewsletter,\n     sub,\n     reducedSub,\n     increasedSub,\n     sub + latePenalty as subIfLate,\n     reducedSub + latePenalty as reducedSubIfLate,\n     increasedSub + latePenalty as increasedSubIfLate\nFROM  view_memberships\nWHERE paid = \'No\'\nAND status=\'Active\'\nORDER BY mailName', '_menu_', 0, '2008-07-25 00:29:08'),
(119, 'test', 'asdf', 'SELECT * from member_updates', 's.pearson', 545, '2008-07-30 23:13:24'),
(120, 'Defunct_Memberships', 'Memberships rendered defunct by coupling/decoupling or resignation-then-rejoining.', 'SELECT \n    firstName,\n    lastName,\n    m.id as memberID,\n    ms.id as MSID,\n    mst.membershipTypeEnum,\n    statusAdmin as status,\n    mm.timestamp as timeWhenDefunct\nFROM \n   members m, \n   memberships ms, \n   members_memberships mm,\n   membership_types mst\nWHERE\n   mm.memberId = m.id AND\n   mm.membershipId = ms.id AND\n   ms.membershipTypeId = mst.id', 'r.lobb', 595, '2008-07-31 00:30:44'),
(121, 'Associate_Couple_Emails', 'Email addresses for all associate couples.', 'SELECT firstName, lastName, primaryEmail from view_members where membershipType = \'AssociateCouple\'\n', 'r.lobb', 595, '2008-07-31 11:25:37'),
(122, 'Memberships_For_Renewals', 'A table of membership data for merging with the MS Word membership-renewal form.', 'SELECT \r\n\r\nvm.*, loginName as login1, workPhone as workphone1, mobilePhone as mobilePhone1, emergencyContactName as emergencyContactName1, emergencyContactPhone as emergencyContactPhone1\r\n\r\nFROM view_memberships as vm\r\nJOIN members on vm.memberId1 = members.id\r\nJOIN membership_types as mst on vm.type = mst.membershipTypeEnum\r\nWHERE vm.status = \'Active\' or vm.status = \'Provisional\'\r\nORDER BY mailName', 's.pearson', 545, '2008-12-29 06:57:37'),
(123, 'Non_Associates_NL_AND_FMC_Bulletin', 'Non Associate members who get a printed newsletter AND an FMC bulletin', 'SELECT \n\n    mailName as name, \n    address1, address2, city, postcode, type,\n    IF(paid = \'No\', \'Unpaid\',\'\') as Paid\n\nFROM\n    view_memberships\n\nWHERE status = \'Active\'\n      AND not (type like \'Associate%\')\n      AND mailNewsletter = \'Yes\'\n      AND mailFMC = \'Yes\'\n\nORDER BY name', 's.pearson', 545, '2009-06-23 04:36:56'),
(124, 'Notes-query-2010', '', 'SELECT view_memberships.membershipId, nameBySurname, membershipNotes, subs_payments.notes\nFROM view_memberships, subs_payments\nWHERE membershipNotes <> \'\'\nAND view_memberships.membershipId = subs_payments.membershipId\nAND subs_payments.subscriptionYear = 2010\n', 'ant.white', 1907, '2010-04-29 09:21:27'),
(125, 'Resignations_this_year', 'List all memberships closures (resignations, struck-offs, deaths, etc) in the current membership year, which starts on February 1.', 'SELECT concat(lastName, \', \', firstName) as \'Name\', membershipTypeEnum as \'Type\', \n    statusAdmin as \'Status\', date(resignationDateAdmin) as \'Resignation date\', date(dateClosedAdmin) as \'Date Closed\',\nmembershipNotes as \'Membership Notes\'\nFROM  members, memberships, membership_types\nWHERE membershipId = memberships.id\nAND memberships.membershipTypeId = membership_types.id\nAND not(statusAdmin =\'Active\')\nAND (\n    (year(resignationDateAdmin) = year(now()) and month(resignationDateAdmin) > 1) OR\n    (month(now()) = 1 AND year(resignationDateAdmin) = year(now()))  OR\n    (month(now()) = 1 AND year(resignationDateAdmin) = year(now()) - 1 AND month(resignationDateAdmin) > 1)\n)\nORDER BY dateClosedAdmin DESC', '_menu_', 0, '2010-06-27 22:26:17'),
(126, 'New_members_this_year', 'A list of members who have joined in the current membership year, namely from 1st February to today. If run in January, returns all new members since previous 1st February.', 'SELECT firstName, lastName, membershipType, dateJoined\nFROM view_members\nWHERE\n    (year(dateJoined) = year(now()) and month(dateJoined) > 1) OR\n    (month(now()) = 1 AND year(dateJoined) = year(now()))  OR\n    (month(now()) = 1 AND year(dateJoined) = year(now()) - 1 AND month(dateJoined) > 1)', '', 0, '2010-07-15 09:59:41'),
(127, 'DefunctMSPayments', 'List subs payments for defunct memberships', 'SELECT concat(FirstName, \' \', lastName) as Name,\n       memberships.id as MSID,\n       amountPaid, paymentDate,\n       cardNumber,\n       secondaryCardNumber,\n       memberships.statusAdmin as status,\n       memberships.dateClosedAdmin as dateClosed,\n       subs_payments.notes as subsNotes,\n       membershipNotes\nFROM subs_payments, members, memberships, members_memberships\nWHERE subs_payments.membershipID = members_memberships.membershipID\nAND members.ID = members_memberships.memberID\nAND memberships.id = members_memberships.membershipID', '', 0, '2010-08-08 11:41:40'),
(128, 'trips_for_stats', 'Listing of selected data for published trips since 1/1/2011 in the events database, used for updating Access database for trip records', 'SELECT id, date, tripLength, leader, grade, title, type, publish\nFROM view_newsletter_events\n\nWHERE type = \'Trip\' AND publish=1 and date >= \'2011-01-01\'', '', 405, '2010-11-16 00:39:47'),
(131, 'SubsEmailMerge', 'Query to use for email-merging of subs invoices.', 'SELECT\n   membershipId as msid,\n   type as membershipType,\n   login1 as login,\n   sub,\n   reducedSub,\n   sub + latePenalty as subIfLate,\n   reducedSub + latePenalty as reducedSubIfLate,\n   increasedSub,\n   increasedSub + latePenalty as increasedSubIfLate,\n   mailName as name,\n   primaryEmail as email,\n   mailNewsletter\nFROM view_memberships\nWHERE  status = \'Active\'\nAND paid = \'No\'\nAND type != \'Life\'\nAND type != \'LifeCouple\'\nAND primaryEmail != \'\';', '', 595, '2011-04-06 10:34:53'),
(132, 'Newish_members', 'A list of all members added to the database in the last 365 days, ordered by addition date, most recent first', 'SELECT *\nFROM view_members\nWHERE datediff(now(), dateJoined) < 365\nORDER BY dateJoined DESC, memberId DESC', '', 1893, '2011-06-26 05:24:02'),
(133, 'UnpaidMembershipsNoEmail', 'A list of all unpaid memberships that aren\'t life memberships and that don\'t have an email address. For printing invoices for distribution with the newsletter.', 'SELECT\n    mailName,\n    concat(type, if(mailNewsletter=\'Yes\', \' (mailed newsletter)\', \' (no mailed newsletter)\')) as type,\n    sub,\n    sub - 5 as reducedSub, \n    mailNewsletter,\n    login1,\n    membershipId,\n    DATE_FORMAT(CURDATE(), \'%d %M %Y\') as today\nFROM\n    ctcweb9_ctc.view_memberships\nWHERE  status=\'Active\'\nAND       paid=\'No\'\nAND       type != \'Life\'\nAND       type != \'LifeCouple\'\nAND       primaryEmail = \'\'\nORDER BY mailName', '', 1967, '2012-04-26 08:28:56'),
(134, 'view_members_for_hacks', 'An expanded version of view_memberships view-query for experimentation', 'SELECT\n    m.id AS memberId,\n    m.membershipId AS membershipId,\n    m.lastName AS lastName,\n    m.firstName AS firstName,\n    concat(m.firstName,  \' \', m.lastName) AS fullName,\n    mst.membershipTypeEnum AS membershipType,\n    ms.statusAdmin AS status,\n    m.notes AS notes,\n    m.dateJoined AS dateJoined,\n    ms.dateOpenedAdmin AS msDateOpened,\n    ms.dateClosedAdmin as msDateClosed,\n    ms.membershipNotes AS membershipNotes\nFROM members as m, memberships as ms, membership_types as mst\nWHERE m.membershipId = ms.id\nAND ms.membershipTypeId = mst.id\nORDER BY m.id', '', 595, '2014-03-01 20:25:33'),
(135, 'New_members_last_year', 'A list of members who joined in the previous membership year, namely from 1st February last year until 1st February this year. If run in January returns new members from 1st February of the year before last up to 1st February last year.', 'SELECT firstName, lastName, membershipType, dateJoined\nFROM view_members\nWHERE\n    (month(now()) > 1 AND year(dateJoined) = year(now()) - 1   AND month(dateJoined) > 1) OR\n    (month(now()) > 1 AND year(dateJoined) = year(now())        AND month(dateJoined) = 1) OR\n    (month(now()) = 1 AND year(dateJoined) = year(now()) - 2   AND month(dateJoined) > 1) OR\n    (month(now()) = 1 AND year(dateJoined) = year(now()) - 1   AND month(dateJoined) = 1)', '', 0, '2014-03-02 07:34:18'),
(136, 'Resignations_last_year', 'A list of member resignations, struck-offs, deaths etc in the membership year prior to the current one.', 'SELECT concat(lastName, \', \', firstName) as \'Name\', membershipTypeEnum as \'Type\', \n    statusAdmin as \'Status\', date(resignationDateAdmin) as \'Resignation date\', date(dateClosedAdmin) as \'Date Closed\',\nmembershipNotes as \'Membership Notes\'\nFROM  members, memberships, membership_types\nWHERE membershipId = memberships.id\nAND memberships.membershipTypeId = membership_types.id\nAND not(statusAdmin =\'Active\')\nAND (\n    (month(now()) > 1 AND year(resignationDateAdmin) = year(now()) - 1   AND month(resignationDateAdmin) > 1) OR\n    (month(now()) > 1 AND year(resignationDateAdmin) = year(now())        AND month(resignationDateAdmin) = 1) OR\n    (month(now()) = 1 AND year(resignationDateAdmin) = year(now()) - 2   AND month(resignationDateAdmin) > 1) OR\n    (month(now()) = 1 AND year(resignationDateAdmin) = year(now()) - 1   AND month(resignationDateAdmin) = 1)\n)\nORDER BY dateClosedAdmin DESC', '', 0, '2014-03-02 07:42:04'),
(137, 'List_members_by_type', 'Get a list of current members, sorted first by membership type, then by joining date.', 'SELECT  * from view_members \nwhere (status = \'Active\' or status = \'Pending\')\nORDER BY membershipType, dateJoined', '', 0, '2014-03-02 07:55:48'),
(138, 'MS_type_changes', 'A list of all members whose membership type has changed in the last 400 days, excluding resignations, couplings, decouplings. This will be changes from Junior to Ordinary, Ordinary to Associate or Life.', '(SELECT concat(members.firstName, \' \', members.lastName) as Name, fieldName as Field, oldValue as Old,\n    newValue as New, member_updates.timestampAdmin as Date, member_updates.notes as Notes, \nadmins.loginName as WhoDunnit\n FROM members, member_updates, members as admins\n WHERE members.id = member_updates.memberId\n AND fieldName = \'MembershipTypeEnum\'\n AND  member_updates.userIdAdmin = admins.id\n AND datediff(now(), member_updates.timeStampAdmin) <= 400)\n\nUNION\n\n(SELECT concat(members.firstName, \' \', members.lastName) as Name, fieldName as Field, oldValue as Old,\n    newValue as New,  membership_updates.timestampAdmin as Date,\n    membership_updates.notes as Notes, \n    admins.loginName as WhoDunnit\n FROM members, membership_updates, memberships, members as admins\n WHERE memberships.id = membership_updates.membershipId \n AND fieldName = \'MembershipTypeEnum\'\n AND memberships.id = members.membershipId\n AND membership_updates.userIdAdmin = admins.id\n AND datediff(now(), membership_updates.timeStampAdmin) <= 400)\n\n ORDER BY Date DESC', '', 0, '2014-03-02 08:05:00'),
(139, 'Email_List', 'A list of membership emails suitable for use with MailChimp', 'SELECT primaryEmail as \'Email Address\', firstName as \'First Name\', lastName as \'Last Name\' \nFROM view_members\nWHERE primaryEmail != \'\'\nAND onEmailList=\'Yes\'\nAND status = \'Active\'', '', 595, '2015-01-06 08:03:48'),
(140, 'Trips_in_last_year', 'A list of the major trip information for all trips run in the last 400 days', 'SELECT title, date, dateDisplay, datePlus, tripLength, leader, leaderPhone, leaderplus, leaderEmail, grade, cost\nFROM ctcweb9_newsletter.events\nWHERE type=\'Trip\'\nAND DATEDIFF(now(), date) < 400;', '', 0, '2016-03-01 09:12:41'),
(141, 'GeneralPurposeSql', 'Used for arbitrary test queries', 'SELECT *\nFROM view_members', '', 595, '2016-07-31 11:17:38'),
(142, 'All_Events_Ever', 'A dump of the newsletter events table.', 'SELECT *\nFROM ctcweb9_newsletter.events', '', 0, '2016-09-28 08:59:59'),
(144, 'ElizabethNoel', '', 'SELECT\n   membershipId as msid,\n   type as membershipType,\n   login1 as login,\n   sub,\n   reducedSub,\n   sub + latePenalty as subIfLate,\n   reducedSub + latePenalty as reducedSubIfLate,\n   increasedSub,\n   increasedSub + latePenalty as increasedSubIfLate,\n   mailName as name,\n   primaryEmail as email,\n   mailNewsletter\nFROM view_memberships \nWHERE  status = \'Active\' \nAND paid = \'No\'\nAND type != \'Life\'\nAND type != \'LifeCouple\'\nAND primaryEmail != \'\'\nAND membershipId=1746;', '', 2218, '2017-05-28 07:22:48'),
(145, 'UnpaidSubsEmail', '', 'SELECT\n   membershipId as msid,\n   type as membershipType,\n   login1 as login,\n   sub,\n   reducedSub,\n   sub + latePenalty as subIfLate,\n   reducedSub + latePenalty as reducedSubIfLate,\n   increasedSub,\n   increasedSub + latePenalty as increasedSubIfLate,\n   mailName,\n   primaryEmail as email,\n   mailNewsletter\nFROM view_memberships\nWHERE  status = \'Active\'\nAND paid = \'No\'\nAND type != \'Life\'\nAND type != \'LifeCouple\'\nAND primaryEmail != \'\'\nORDER BY mailName;', '', 2218, '2017-08-09 07:53:06'),
(146, 'TripLeaderEmailList', 'Return a table of all members on the Trip Leaders Email List.', 'SELECT fullName, primaryEmail\nFROM view_members \nWHERE onTripLeaderEmailList = \'Yes\'\nORDER BY fullName', '', 0, '2018-03-25 00:35:33'),
(147, 'SubsEmailMergeWithEmergencyContacts', 'Query to use for email-merging of subs invoices.', 'SELECT\n   membershipId as msid,\n   type as membershipType,\n   login1 as login,\n   sub,\n   reducedSub,\n   sub + latePenalty as subIfLate,\n   reducedSub + latePenalty as reducedSubIfLate,\n   increasedSub,\n   increasedSub + latePenalty as increasedSubIfLate,\n   mailName as name,\n   primaryEmail as email,\n   mailNewsletter,\n   isCouple,\n   emergencyContactName1,\n   emergencyContactPhone1,\n   emergencyContactName2,\n   emergencyContactPhone2\nFROM view_memberships\nWHERE  status = \'Active\'\nAND paid = \'No\'\nAND type != \'Life\'\nAND type != \'LifeCouple\'\nAND type != \'PseudoMember\'\nAND primaryEmail != \'\';', '', 2218, '2018-04-19 10:09:52');
INSERT INTO `viewcreate_sqls` (`id`, `viewName`, `createViewSql`) VALUES
(1, 'view_single_membership_names', 'CREATE OR REPLACE VIEW view_single_membership_names AS\r\nSELECT members.membershipid AS membershipid, \r\n       members.id           AS memberid1, \r\n       0                    AS memberid2, \r\n       members.loginname    AS login1, \r\n       \'\'                   AS login2,\r\n       IF(Isnull(memberships.membershipmailname)  OR memberships.membershipmailname = \'\',\r\n           Concat(members.firstname,\' \',members.lastname), memberships.membershipmailname) \r\n         AS mailname,\r\n       concat(members.lastname,\', \',members.firstname) AS namebysurname\r\nFROM memberships\r\nJOIN members ON memberships.id = members.membershipid \r\nJOIN view_single_membership_ids as vsmids ON vsmids.membershipId = memberships.id'),
(2, 'view_couple_membership_names', 'create view view_couple_membership_names as\nselect memberships.id as membershipId,\nm1.id as memberId1,\nm2.id as memberId2,\nm1.loginName as login1,\nm2.loginName as login2,\nif((isnull(membershipMailName) or (membershipMailName=\'\')),\n   if((m1.lastName = m2.lastName),\n        concat(concat(m1.firstName,\' & \',m2.firstName),\' \',m1.lastName),\n        concat(m1.firstName,\' \',m1.lastName,\' & \',m2.firstName,\' \',m2.lastName)),\n   membershipMailName) AS mailName,\n   if((m1.lastName = m2.lastName),concat(m1.lastName,\', \',concat(m1.firstName,\' & \',m2.firstName)),\n   concat(m1.lastName,\', \',m1.firstName,\' & \',m2.lastName,\', \',m2.firstName)) AS nameBySurname\n\nfrom memberships join members m1 join members m2\non m1.membershipId = m2.membershipId\nand memberships.id = m1.membershipId\nand memberships.id = m2.membershipId\nand m1.id < m2.id\n'),
(3, 'view_membership_names', 'CREATE VIEW view_membership_names as\nSELECT \n  membershipId, \n  memberId1,\n  memberId2,\n  login1,\n  login2,\n  mailName, \n  nameBySurname,\n  \'Yes\' as isCouple\nFROM view_couple_membership_names\n\nUNION\n\nSELECT \n  membershipId,\n  memberId1,\n  memberId2,\n  login1, \n  login2,\n  mailName,\n  nameBySurname,\n  \'No\' as isCouple\nFROM view_single_membership_names\n\nORDER BY membershipId'),
(4, 'view_memberships', 'create or replace view view_memberships as\r\nSELECT\r\n  memberships.id as membershipId,\r\n  membershipTypeEnum as type,\r\n  memberId1,\r\n  memberId2,\r\n  login1,\r\n  login2,\r\n  mailName,\r\n  nameBySurname,\r\n  isCouple,\r\n  statusAdmin AS status,\r\n  address1,\r\n  address2,\r\n  city,\r\n  postcode,\r\n  homePhone,\r\n  primaryEmail,\r\n  mailNewsletterBool as mailNewsletter,\r\n  mailFMCBool as mailFMC,\r\n  isFmcMember,\r\n  membershipFee + if(mailNewsletterBool=\'Yes\',newsletterFee,0) as sub,\r\n  membershipFee as reducedSub,\r\n  membershipFee + newsletterFee as increasedSub,\r\n  lateFee as latePenalty,\r\n  if(isnull(subs.id) and membershipFee > 0,\'No\',\'Yes\') as paid,\r\n  paymentDate,\r\n  dateOpenedAdmin as msDateOpened,\r\n  resignationDateAdmin as resignationDate,\r\n  dateClosedAdmin as dateClosed,\r\n  membershipNotes\r\n  \r\nFROM\r\n  memberships JOIN view_membership_names ON memberships.id = view_membership_names.membershipId\r\n  JOIN membership_types on membershipTypeId = membership_types.id\r\n  JOIN members ON members.id = memberID1\r\n  LEFT JOIN subs_payments subs ON\r\n  memberships.id = subs.membershipId AND\r\n  subs.subscriptionYear = year(now()- interval 3 month)\r\n  and not deletedBoolAdmin\r\n  \r\nORDER BY membershipId'),
(5, 'view_members', 'CREATE OR REPLACE VIEW view_members AS\r\nSELECT\r\n  m.id as memberId,\r\n  m.membershipId as membershipId,\r\n  lastName,\r\n  firstName,\r\n  concat(firstName,\' \',lastName) as fullName,\r\n  type as membershipType,\r\n  status,\r\n  loginName,\r\n  m.primaryEmail as primaryEmail,\r\n  homePhone as homePhone,\r\n  workPhone,\r\n  mobilePhone,\r\n  CASE preferredPhoneEnum\r\n       WHEN \'Home\' THEN homePhone\r\n       WHEN \'Mobile\' THEN mobilePhone\r\n       WHEN \'Work\' THEN workPhone\r\n  END AS preferredPhone,\r\n  mailName as membershipMailName,\r\n  emergencyContactName,\r\n  emergencyContactPhone,\r\n  onEmailListBool as onEmailList,\r\n  onTripLeaderEmailListBool as onTripLeaderEmailList,\r\n  m.notes as notes,\r\n  dateJoined,\r\n  address1,\r\n  address2,\r\n  city,\r\n  postcode,\r\n  msDateOpened,\r\n  membershipNotes,\r\n  paid\r\n  \r\nFROM  members as m JOIN view_memberships as ms\r\nON m.membershipId = ms.membershipId\r\n\r\nORDER BY memberId'),
(6, 'view_phplist', 'ALTER VIEW view_phplist AS\r\nSELECT\r\n  uu.foreignkey AS foreignKey,\r\n  uu.email AS email,\r\n  uu.htmlemail as htmlEmail\r\nFROM ctcweb9_plst1.phplist_user_user uu\r\nJOIN ctcweb9_plst1.phplist_listuser listuser\r\nJOIN ctcweb9_plst1.phplist_list list\r\nWHERE listuser.userid = uu.id\r\nAND listuser.listid = list.id\r\nAND list.name = \'members\'\r\nAND uu.confirmed = 1\r\nORDER BY email'),
(12, 'phplist_listuser', 'CREATE VIEW phplist_listuser AS\r\nSELECT \r\n   id as userid,\r\n   2 as listid,\r\n   entered as entered,\r\n   now() as modified\r\nFROM phplist_user_user'),
(7, 'view_newsletter_events', 'CREATE VIEW view_newsletter_events AS\r\nSELECT  *\r\nFROM ctcweb9_newsletter.events'),
(8, 'view_jos_users', 'CREATE VIEW `jos_users` as\r\n\r\n\r\n\r\nSELECT\r\n\r\n    members.id,\r\n\r\n    concat(firstName, \' \', lastName) as name,\r\n\r\n    loginName as username,\r\n\r\n    primaryEmail as email,\r\n\r\n    joomlaPasswordAdmin as password,\r\n\r\n    joomlaUserTypeEnumAdmin as usertype,\r\n\r\n    0 as block,\r\n\r\n    0 as sendEmail,\r\n\r\n    groups.group_id as gid,\r\n\r\n    dateJoined as registerDate,\r\n\r\n    joomlaLastVisitDateAdmin as lastVisitDate,\r\n\r\n    \'\' as activation,\r\n\r\n    joomlaParamsAdmin as params \r\n\r\n    \r\n\r\nFROM ctcweb9_ctc.members as members, ctcweb9_ctc.memberships as mships, ctcweb9_joom1.jos_core_acl_aro_groups as groups\r\n\r\nWHERE\r\n\r\n    members.joomlaUserTypeEnumAdmin = groups.name AND\r\n\r\n    members.membershipId = mships.id AND\r\n\r\n    primaryEmail is not NULL AND\r\n\r\n    mships.statusAdmin = \'Active\''),
(9, 'view_jos_ctc_member_data', 'CREATE VIEW `jos_ctc_member_data` as\r\n\r\n\r\n\r\nSELECT\r\n\r\n    members.id as _id,\r\n\r\n    membershipId as _membershipId,\r\n\r\n    firstName,\r\n\r\n    lastName,\r\n\r\n    loginName,\r\n\r\n    primaryEmail as email,\r\n\r\n    homePhone,\r\n\r\n    workPhone,\r\n\r\n    mobilePhone,\r\n\r\n    emergencyContactName,\r\n\r\n    emergencyContactPhone,\r\n\r\n    onEmailListBool as onClubEmailList,\r\n\r\n    htmlEmailBool as receiveEmailInHtml,\r\n\r\n    address1,\r\n\r\n    address2,\r\n\r\n    city,\r\n\r\n    postcode\r\n\r\n    \r\n\r\nFROM ctcweb9_ctc.members as members, ctcweb9_ctc.memberships as mships\r\n\r\nWHERE\r\n\r\n    members.membershipId = mships.id AND\r\n\r\n    mships.statusAdmin = \'Active\'\r\n\r\n    '),
(10, 'view_jos_core_acl_aro', 'CREATE VIEW `jos_core_acl_aro` AS\r\n\r\n\r\n\r\nSELECT\r\n\r\n    id as aro_id,\r\n\r\n    \'users\' as section_value,\r\n\r\n    cast(id as char(11)) as value,\r\n\r\n    0 as order_value,\r\n\r\n    name,\r\n\r\n    0 as hidden\r\n\r\nFROM jos_users'),
(11, 'view_jos_core_acl_groups_aro_map', 'CREATE VIEW `jos_core_acl_groups_aro_map` AS\r\n\r\n\r\n\r\nSELECT\r\n\r\n     group_id,\r\n\r\n     \'\' as section_value,\r\n\r\n     id as aro_id\r\n\r\nFROM jos_users as users, jos_core_acl_aro_groups as groups\r\n\r\nWHERE users.usertype = groups.name'),
(13, 'phplist_user_user', 'CREATE VIEW phplist_user_user AS\r\nSELECT   mb.id,\r\n   primaryEmail as email,\r\n   1 as confirmed,\r\n   0 as blacklisted,\r\n   bounceCountAdmin as bouncecount,\r\n   datejoined as entered,\r\n   now() as modified,\r\n   format(mb.id, 0) as uniqid,\r\n   if(htmlEmailBool = \'Yes\', 1, 0) as htmlemail,\r\n   NULL as subscribepage,\r\n   0 as rssfrequency,\r\n   \'notToBeUsedDamm1t\' as password,\r\n   NULL as passwordchanged,\r\n   0 as disabled,\r\n   NULL as extradata,\r\n   mb.id  as foreignkey\r\nFROM ctcweb9_ctc.members AS mb\r\nJOIN ctcweb9_ctc.memberships as ms\r\nON mb.membershipId = ms.id\r\nWHERE ms.statusAdmin=\'Active\'\r\nAND onEmailListBool=\'Yes\'\r\nORDER BY mb.id'),
(14, 'view_committee', 'CREATE OR REPLACE VIEW `view_committee`  AS\r\nSELECT   `view_members`.`fullname`       AS `fullname`, \r\n         `roles`.`role`                  AS `role`, \r\n         `view_members`.`preferredphone` AS `preferredphone`, \r\n         `view_members`.`homephone`      AS `homephone`, \r\n         `view_members`.`mobilephone`    AS `mobilephone`, \r\n         `view_members`.`primaryemail`   AS `email` \r\nFROM     ((`members_roles` \r\nJOIN     `roles`) \r\nJOIN     `view_members`) \r\nWHERE    ((`members_roles`.`memberid` = `view_members`.`memberid`) \r\n         AND (`members_roles`.`roleid` = `roles`.`id`) \r\n         AND `roles`.`iscommittee` \r\n         AND (`roles`.`role` <> \'Committee\') \r\n         AND (`roles`.`role` <> \'Club Patron\')) \r\nORDER BY `roles`.`newsletter_order`'),
(15, 'view_single_membership_ids', 'CREATE OR REPLACE VIEW view_single_membership_ids AS\r\nSELECT members.membershipId AS membershipId\r\nFROM memberships join members on memberships.id = members.membershipId\r\nGROUP BY members.membershipId\r\nHAVING count(*) = 1\r\nORDER BY members.membershipId');
